import React,{useState,useEffect} from "react"
const useForm = (initFieldValues) => {
    const [values,setValues]= useState(initFieldValues)
    const handleInputChange = e =>{
        const {name,value}= e.target
        setValues({
            ...values,
            [name]:value
        })
    }
    return {
        values,
        setValues,
        handleInputChange
    };
}

export default useForm;
