import React,{useState,useEffect} from "react";
import { connect } from  "react-redux";
import * as actions  from "../actions/DCandidate"
import {Grid,Paper,TableContainer,Table,TableHead,TableBody,TableRow,TableCell,withStyles, ButtonGroup,Button} from "@material-ui/core";
import DCandidateForm from "./DCandidateForm";
import EditIcon from "@material-ui/icons/Edit";
import DeleteIcon from "@material-ui/icons/Delete";

const styles = theme =>({
    paper:{
        margin:theme.spacing(2),
        padding:theme.spacing(2)
    },
    root:{
        "& .MuiTableCell-head":{
            fontSize:"1.25rem",
            fontWeight: "bold",
            color:"green"
        }
    }
})

const DCandidates = ( {classes,...props} )=>{
    const [currentId,setCurrentId]= useState(0)

    useEffect(()=>{
        props.fetchAllDCandidates()
    },[])
    return (
    <Paper className={classes.paper} elevation={3}>
        <Grid container>
            <Grid item={true} xs={6}>
                <DCandidateForm {...({currentId,setCurrentId})}></DCandidateForm>
            </Grid>
            <Grid item={true} xs={6}>
                <TableContainer>
                    <Table>
                        <TableHead className={classes.root}>
                            <TableRow>
                                <TableCell>Name</TableCell>
                                <TableCell>Phone</TableCell>
                                <TableCell>Blood Type</TableCell>
                                <TableCell></TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {
                                props.dCandidateData.map((record,index)=>{
                                    return (
                                        <TableRow key={index} hover>
                                            <TableCell>{record.fullName}</TableCell>
                                            <TableCell>{record.phone}</TableCell>
                                            <TableCell>{record.bloodGroup}</TableCell>
                                            <TableCell>
                                                <ButtonGroup variant="text">
                                                    <Button onClick={()=>{setCurrentId(record.id)}}>
                                                        <EditIcon color="primary"></EditIcon>
                                                    </Button>
                                                    <Button>
                                                        <DeleteIcon color="secondary"></DeleteIcon>
                                                    </Button>
                                                </ButtonGroup>
                                            </TableCell>
                                        </TableRow>
                                    );
                                })
                            
                            }
                        </TableBody>
                    </Table>
                </TableContainer>
            </Grid>
        </Grid>
    </Paper>);
}

const mapStateToProps = state => ({
    dCandidateData:state.dCandidate.data
});

const mapActionToProps = {
    fetchAllDCandidates:actions.fetchall,
    deleteDCandidate:actions.Delete
}

export default connect(mapStateToProps,mapActionToProps)(withStyles(styles)(DCandidates));
