import React,{useState,useEffect} from "react"
const useForm = (initFieldValues) => {
    const [values,setValues]= useState(initFieldValues)
    const inputLabel = React.useRef(null);
    const [labelWidth,setLabelWidth]= React.useState(0);
    React.useEffect(()=>{setLabelWidth(inputLabel.current.offsetWidth)})

    const handleInputChange = e =>{
        const {name,value}= e.target
        setValues({
            ...values,
            [name]:value
        })
    }
    const handleFormSubmit = e => {
        e.preventDefault()
        console.log(values);    
    }
    const [errors,setErrors] = useState({})

    const validateForm = (fieldValidation) =>{//key:validation pattern?
        let temp={}
        fieldValidation.map((ele,key)=>{//just check for a value
            temp[key]=values[key]?"":"This field is required"
        })
        
    }
    return {
        values,
        setValues,
        handleInputChange,
        inputLabel,
        labelWidth,
        setLabelWidth,
        handleFormSubmit, 
        validateForm,
        errors,
        setErrors    
    };
}

export default useForm;
