import React,{useState,useEffect} from "react"
const useForm = (initFieldValues) => {
    let validationPatterns={}
    const setValidationPatterns= p=>{//{key:{required:boolean,pattern: regex string}}
        validationPatterns={...p}
    }
    const [values,setValues]= useState(initFieldValues)
    const inputLabel = React.createRef();
    const [labelWidth,setLabelWidth]= React.useState(0);
    React.useEffect(()=>{setLabelWidth(inputLabel.current.offsetWidth)})

    const handleInputChange = e =>{
        const {name,value}= e.target
        console.log(name)
        setValues({
            ...values,
            [name]:value
        })
        if(validationPatterns[name])
            validateForm({...{[name]:validationPatterns[name]}})
    }
    const handleFormSubmit = e => {
        e.preventDefault()
        console.log(values);    
        if(validateForm())
        {
            window.alert('Validated!')
        }
    }
    const [errors,setErrors] = useState({})

    const validateForm = (fieldValidation=validationPatterns) =>{//{key:{required: boolean,pattern:...},...}
        let temp={}
        Object.keys(fieldValidation).map((key,i)=>{//just check for a value
            if(fieldValidation[key].required && !values[key])
            {
                temp[key]=values[key]?"":"This field is required."
            }
            if(values[key] && fieldValidation[key].pattern)
            {
                const regEx=RegExp(fieldValidation[key].pattern,"g")
                temp[key] = regEx.test(values[key])?"":"Invalid value"
            }
            
        })
        console.log(temp)
        setErrors({
            ...temp
        })
        if(fieldValidation==validationPatterns)
            return Object.values(temp).every(x=> x == "")
    }
    return {
        values,
        setValues,
        handleInputChange,
        inputLabel,
        labelWidth,
        setLabelWidth,
        handleFormSubmit, 
        validateForm,
        validationPatterns,
        setValidationPatterns,
        errors,
        setErrors    
    };
}

export default useForm;
