import React,{useState} from "react";
import {Grid,InputLabel,TextField, withStyles,FormControl,Select,MenuItem} from "@material-ui/core";
import useForm from "./useForm"

const initFieldValues = {
    fullName:"",
    phone:"",
    email:"",
    address:"",
    age:"",
    bloodGroup:""
}
const styles = theme => ({
    root:{
        '& .MuiTextField-root':{
            margin: theme.spacing(1),
            minWidth:230
        }
    }
})
const DCandidateForm = ( {classes, ...props} ) => {
   
    const {
        values,
        setValues,
        handleInputChange
    } = useForm(initFieldValues)    
    const BloodTypes=["A+","A-","B+","B-","AB+","AB-","O+","O-"]
    return (  
    <form autoComplete="off" noValidate className={classes.root}>
        <Grid container>
            <Grid item xs={6}>
                <TextField id="dcandidate_fullname" name="fullName" variant="outlined" label="Full Name" value={values.fullName} onchange={handleInputChange}/>
                <TextField id="dcandidate_email" name="email" variant="outlined" label="Email" value={values.email} onchange={handleInputChange}/>
                <FormControl variant="outlined">
                    <InputLabel>Blood Type</InputLabel>
                    <Select id="dcandidate_bloodgroup" name="bloodGroup" value={values.bloodGroup} onchange={handleInputChange}>
                        {
                            BloodTypes.map((record)=>{
                                return (
                                    <MenuItem value={record}>{record}</MenuItem>
                                )
                            })
                        }
                    </Select>
                </FormControl>
            </Grid>
            <Grid item xs={6}>
            <TextField id="dcandidate_phone" name="phone" variant="outlined" label="Phone" value={values.phone} onchange={handleInputChange}/>
            <TextField id="dcandidate_address" name="address" variant="outlined" label="Address" value={values.address} onchange={handleInputChange}/>
            <TextField id="dcandidate_age" name="age" variant="outlined" label="Age" value={values.age} onchange={handleInputChange}/>
            </Grid>
        </Grid>
    </form> );
}

export default withStyles(styles)(DCandidateForm);
